import React, { useState } from 'react';
import { 
  ChevronRight, 
  ChevronDown, 
  File, 
  Folder, 
  FolderOpen,
  FileCode
} from 'lucide-react';

interface FileExplorerProps {
  onFileSelect: (filePath: string) => void;
}

interface FileNode {
  name: string;
  type: 'file' | 'folder';
  path: string;
  children?: FileNode[];
}

const FileExplorer: React.FC<FileExplorerProps> = ({ onFileSelect }) => {
  const [expandedNodes, setExpandedNodes] = useState<Set<string>>(new Set());
  const [selectedFile, setSelectedFile] = useState<string | null>(null);

  // Mock data structure
  const mockFiles: FileNode[] = [
    {
      name: 'src',
      type: 'folder',
      path: 'src',
      children: [
        {
          name: 'components',
          type: 'folder',
          path: 'src/components',
          children: [
            { name: 'App.tsx', type: 'file', path: 'src/components/App.tsx' },
            { name: 'Button.tsx', type: 'file', path: 'src/components/Button.tsx' }
          ]
        },
        {
          name: 'utils',
          type: 'folder',
          path: 'src/utils',
          children: [
            { name: 'helpers.py', type: 'file', path: 'src/utils/helpers.py' },
            { name: 'helpers.pyh.json', type: 'file', path: 'src/utils/helpers.pyh.json' }
          ]
        },
        { name: 'index.tsx', type: 'file', path: 'src/index.tsx' }
      ]
    },
    {
      name: 'backend',
      type: 'folder',
      path: 'backend',
      children: [
        { name: 'app.py', type: 'file', path: 'backend/app.py' },
        { name: 'app.pyh.json', type: 'file', path: 'backend/app.pyh.json' }
      ]
    },
    { name: 'README.md', type: 'file', path: 'README.md' }
  ];

  const toggleNode = (nodePath: string) => {
    const newExpanded = new Set(expandedNodes);
    if (newExpanded.has(nodePath)) {
      newExpanded.delete(nodePath);
    } else {
      newExpanded.add(nodePath);
    }
    setExpandedNodes(newExpanded);
  };

  const getFileIcon = (fileName: string) => {
    if (fileName.endsWith('.py')) {
      return <FileCode className="h-4 w-4 text-primary-500" />;
    } else if (fileName.endsWith('.pyh.json')) {
      return <FileJson className="h-4 w-4 text-yellow-500" />;
    } else if (fileName.endsWith('.json')) {
      return <FileJson className="h-4 w-4 text-neutral-500" />;
    } else {
      return <File className="h-4 w-4 text-neutral-500" />;
    }
  };

  const filterFiles = (files: FileNode[]): FileNode[] => {
    if (!searchTerm) return files;

    return files.filter(file => {
      if (file.type === 'directory') {
        const filteredChildren = filterFiles(file.children || []);
        return filteredChildren.length > 0 || file.name.toLowerCase().includes(searchTerm.toLowerCase());
      }
      return file.name.toLowerCase().includes(searchTerm.toLowerCase());
    }).map(file => {
      if (file.type === 'directory') {
        return { ...file, children: filterFiles(file.children || []) };
      }
      return file;
    });
  };

  const renderNode = (node: FileNode, level: number = 0): React.ReactNode => {
    const isExpanded = expandedNodes.has(node.path);
    const isSelected = repository.selectedFile === node.path;
    const hasChildren = node.children && node.children.length > 0;
    const isDirectory = node.type === 'directory';

    return (
      <div key={node.path}>
        <div
          className={`flex items-center py-1 px-2 cursor-pointer hover:bg-neutral-100 transition-colors ${
            isSelected ? 'bg-primary-50 border-r-2 border-primary-500' : ''
          }`}
          style={{ paddingLeft: `${level * 16 + 8}px` }}
          onClick={() => {
            if (isDirectory) {
              toggleNode(node.path);
            } else {
              selectFile(node.path);
            }
          }}
        >
          {isDirectory && (
            <div className="mr-1">
              {isExpanded ? (
                <ChevronDown className="h-3 w-3 text-neutral-500" />
              ) : (
                <ChevronRight className="h-3 w-3 text-neutral-500" />
              )}
            </div>
          )}

          <div className="mr-2">
            {isDirectory ? (
              isExpanded ? (
                <FolderOpen className="h-4 w-4 text-primary-500" />
              ) : (
                <Folder className="h-4 w-4 text-primary-500" />
              )
            ) : (
              getFileIcon(node.name)
            )}
          </div>

          <span className={`text-sm truncate ${
            isSelected ? 'text-primary-700 font-medium' : 'text-neutral-700'
          }`}>
            {node.name}
          </span>
        </div>

        {isDirectory && isExpanded && hasChildren && (
          <div>
            {node.children!.map(child => renderNode(child, level + 1))}
          </div>
        )}
      </div>
    );
  };

  const filteredFiles = filterFiles(repository.files);

  return (
    <div className="w-80 bg-white border-r border-neutral-200 flex flex-col h-full">
      {/* Header */}
      <div className="p-3 border-b border-neutral-200">
        <h2 className="text-sm font-semibold text-neutral-800 mb-2">Explorer</h2>

        {/* Search */}
        <div className="relative">
          <Search className="absolute left-2 top-1/2 transform -translate-y-1/2 h-3 w-3 text-neutral-400" />
          <input
            type="text"
            placeholder="Search files..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-7 pr-3 py-1.5 text-xs border border-neutral-300 rounded focus:outline-none focus:ring-1 focus:ring-primary-500"
          />
        </div>
      </div>

      {/* File tree */}
      <div className="flex-1 overflow-y-auto">
        {filteredFiles.length > 0 ? (
          filteredFiles.map(node => renderNode(node))
        ) : (
          <div className="p-4 text-center text-sm text-neutral-500">
            {repository.isConnected ? 'No files found' : 'Clone a repository to get started'}
          </div>
        )}
      </div>
    </div>
  );
};

export default FileExplorer;
