import React, { useState } from 'react';
import { 
  ChevronRight, 
  ChevronDown, 
  File, 
  Folder, 
  FolderOpen,
  FileCode
} from 'lucide-react';

interface FileExplorerProps {
  onFileSelect: (filePath: string) => void;
}

interface FileNode {
  name: string;
  type: 'file' | 'folder';
  path: string;
  children?: FileNode[];
}

const FileExplorer: React.FC<FileExplorerProps> = ({ onFileSelect }) => {
  const [expandedNodes, setExpandedNodes] = useState<Set<string>>(new Set());
  const [selectedFile, setSelectedFile] = useState<string | null>(null);

  // Mock data structure
  const mockFiles: FileNode[] = [
    {
      name: 'src',
      type: 'folder',
      path: 'src',
      children: [
        {
          name: 'components',
          type: 'folder',
          path: 'src/components',
          children: [
            { name: 'App.tsx', type: 'file', path: 'src/components/App.tsx' },
            { name: 'Button.tsx', type: 'file', path: 'src/components/Button.tsx' }
          ]
        },
        {
          name: 'utils',
          type: 'folder',
          path: 'src/utils',
          children: [
            { name: 'helpers.py', type: 'file', path: 'src/utils/helpers.py' },
            { name: 'helpers.pyh.json', type: 'file', path: 'src/utils/helpers.pyh.json' }
          ]
        },
        { name: 'index.tsx', type: 'file', path: 'src/index.tsx' }
      ]
    },
    {
      name: 'backend',
      type: 'folder',
      path: 'backend',
      children: [
        { name: 'app.py', type: 'file', path: 'backend/app.py' },
        { name: 'app.pyh.json', type: 'file', path: 'backend/app.pyh.json' }
      ]
    },
    { name: 'README.md', type: 'file', path: 'README.md' }
  ];

  const toggleNode = (nodePath: string) => {
    const newExpanded = new Set(expandedNodes);
    if (newExpanded.has(nodePath)) {
      newExpanded.delete(nodePath);
    } else {
      newExpanded.add(nodePath);
    }
    setExpandedNodes(newExpanded);
  };

  const handleFileClick = (filePath: string) => {
    setSelectedFile(filePath);
    onFileSelect(filePath);
  };

  const getFileIcon = (fileName: string) => {
    if (fileName.endsWith('.py')) {
      return <FileCode className="h-4 w-4 text-blue-500" />;
    } else {
      return <File className="h-4 w-4 text-gray-500" />;
    }
  };

  const renderNode = (node: FileNode, level: number = 0): React.ReactNode => {
    const isExpanded = expandedNodes.has(node.path);
    const isSelected = selectedFile === node.path;
    const hasChildren = node.children && node.children.length > 0;
    const isFolder = node.type === 'folder';

    return (
      <div key={node.path}>
        <div
          className={`flex items-center py-1 px-2 cursor-pointer hover:bg-gray-100 transition-colors ${
            isSelected ? 'bg-blue-50 border-r-2 border-blue-500' : ''
          }`}
          style={{ paddingLeft: `${level * 16 + 8}px` }}
          onClick={() => {
            if (isFolder) {
              toggleNode(node.path);
            } else {
              handleFileClick(node.path);
            }
          }}
        >
          {isFolder && (
            <div className="mr-1">
              {isExpanded ? (
                <ChevronDown className="h-3 w-3 text-gray-500" />
              ) : (
                <ChevronRight className="h-3 w-3 text-gray-500" />
              )}
            </div>
          )}

          <div className="mr-2">
            {isFolder ? (
              isExpanded ? (
                <FolderOpen className="h-4 w-4 text-blue-500" />
              ) : (
                <Folder className="h-4 w-4 text-blue-500" />
              )
            ) : (
              getFileIcon(node.name)
            )}
          </div>

          <span className={`text-sm truncate ${
            isSelected ? 'text-blue-700 font-medium' : isFolder ? 'font-bold text-gray-800' : 'text-gray-700'
          }`}>
            {node.name}
          </span>
        </div>

        {isFolder && isExpanded && hasChildren && (
          <div>
            {node.children!.map(child => renderNode(child, level + 1))}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="w-64 bg-gray-50 border-r border-gray-300 h-full overflow-y-auto">
      <div className="p-3">
        <h3 className="text-sm font-semibold text-gray-800 mb-2">Files</h3>
        {mockFiles.map(node => renderNode(node))}
      </div>
    </div>
  );
};

export default FileExplorer;
